import React from 'react';
import {getbubblesortanimation} from '../sortingAlgorithms/BubbleSort';
import './SortingVisualizer.css';

// Change this value for the speed of the animations.
const ANIMATION_SPEED_MS = 1;

// Change this value for the number of bars (value) in the array.
const NUMBER_OF_ARRAY_BARS = 310;

// This is the main color of the array bars.
const PRIMARY_COLOR = 'turquoise';

// This is the color of array bars that are being compared throughout the animations.
const SECONDARY_COLOR = 'red';

export default class SortingVisualizer extends React.Component{
    constructor(props){
        super (props);
        this.state = {
            array: [],
        };
    }
    componentDidMount(){
        this.resetArray();
    }
    resetArray(){
        const array = [];
        for(let i=0;i<NUMBER_OF_ARRAY_BARS;i++)
        {
            array.push(randomIntFromInterval(5,730));
        }
        this.setState({array});
    }
    BubbleSort()
    {
        const [animations,sortedarray]=getbubblesortanimation(this.state.array);
        for(let i=0;i<animations.length;i++)
        {
            const iscolorchange= animations[i][0]=="comparison1" || animations[i][0]=="comparison2";
            const arraybars = document.getElementsByClassName('array-bar')
        }
    }
    render(){
        const {array}=this.state;
        return (<div>
        <div className="App-header">
        <div className="heading">
        <h2>Sorting Visualizer</h2>
        </div>
      <div className="button" onClick={()=>this.resetArray()}> Generate an Array </div>
      <div className="button"> Merge Sort </div>
      <div className="button"> Bubble Sort </div>
      <div className="button"> Heap Sort </div>
      </div>
        <div className="array-container">
            {array.map((value,idx)=>(
                <div
                className="array-bar"
                key={idx}
                style={{
                    backgroundColor:PRIMARY_COLOR,
                    height: `${value}px`,
                }}></div>
            ))
            }
        </div>
        </div>);
    }
}
function randomIntFromInterval(min, max) {
    // min and max included
    return Math.floor(Math.random() * (max - min + 1) + min);
  }
