        <div className = "App-header-2">
        <div className = "heading">
        <h2>Searching Visualizer</h2>
        </div>
        <div className = "button"> <input className="element-input" value={this.state.value} onChange={this.LinearSearch()}></input></div>
        <div className = "button" onClick={() => this.resetArray()}> Generate an Array </div>
        <div className = "button" onClick={() => this.LinearSearch()}> Linear Search </div>
        <div className = "button" onClick={() => this.BubbleSort()}> Binary Search </div>
        <div className = "button" onClick={() => this.InsertionSort()}> Exponential Search </div>
        <div className = "button" onClick={() => this.MergeSort()}> Jump Search </div>
        </div>
        <div className ="array-container-2">
            {array.map((value,idx) => (
                <div
                className = "array-bar-2"
                key = {idx}
                style = {{
                    backgroundColor:PRIMARY_COLOR,
                }}>
                    {value}
                </div>
            ))
            }
        </div>
        <div className="footer-2">
      </div>
        </div>);
    }
}
